import Swal from 'sweetalert2';
let MenuViewComponent = class MenuViewComponent {
    constructor(authService) {
        this.authService = authService;
        this.categories = [];
        this.allProducts = [];
        this.arrOrder = [];
        this.data = {};
        this.totalPriceOrder = 0;
    }
    // Get products into an array
    ngOnInit() {
        this.authService.getProducts().subscribe({
            next: response => {
                this.allProducts = response;
                this.categories = this.allProducts;
            },
            error: error => {
                console.error('There was an error!', error);
            }
        });
    }
    filterCategories(e) {
        const type = e.target.dataset.value;
        if (type != '') {
            this.categories = this.allProducts.filter((element) => element.type === type);
        }
        else {
            this.categories = this.allProducts;
        }
        return this.categories;
    }
    addProduct(product) {
        const result = this.arrOrder.find(((x) => x._id == product._id));
        if (result == undefined) {
            this.arrOrder.push(Object.assign(Object.assign({}, product), { qty: 1, totalPrice: product.price, totalPriceOrder: 0 }));
        }
        else {
            this.arrOrder = this.arrOrder.map((x) => x._id == product._id ? Object.assign(Object.assign({}, x), { qty: x.qty + 1, totalPrice: x.price * (x.qty + 1) }) : x);
        }
        this.arrTotalPrice();
    }
    addQty(order) {
        this.arrOrder = this.arrOrder.map((x) => x._id == order._id ? Object.assign(Object.assign({}, x), { qty: x.qty + 1, totalPrice: x.price * (x.qty + 1) }) : x);
        this.arrTotalPrice();
    }
    deleteQty(order) {
        if (order.qty != 1) {
            this.arrOrder = this.arrOrder.map((x) => x._id == order._id ? Object.assign(Object.assign({}, x), { qty: x.qty - 1, totalPrice: x.price * (x.qty - 1) }) : x);
            this.arrTotalPrice();
        }
    }
    deleteProduct(product) {
        this.arrOrder = this.arrOrder.filter((x) => x !== product);
        this.arrTotalPrice();
    }
    deleteOrder() {
        this.arrOrder.length = 0;
        this.totalPriceOrder = 0;
        this.inputName.nativeElement.value = '';
    }
    eachProduct() {
        const detailProductData = this.arrOrder.map((e) => {
            this.data = {
                qty: e.qty,
                productId: e._id
            };
            return this.data;
        });
        return detailProductData;
    }
    arrTotalPrice() {
        this.totalPriceOrder = this.arrOrder.map((e) => {
            return e.totalPrice;
        });
        return this.totalPriceOrder = this.totalPriceOrder.reduce((acc, num) => acc + num);
    }
    addNewOrder() {
        let valueInput = this.inputName.nativeElement.value;
        const idUser = localStorage.getItem('idUser');
        const objOrder = {
            "userId": idUser,
            "client": valueInput,
            "products": this.eachProduct()
        };
