import { Component } from '@angular/core';
import { AuthService } from 'src/app/_services/auth.service';
import Swal from 'sweetalert2';
let OrdersViewComponent = class OrdersViewComponent {
    constructor(authService) {
        this.authService = authService;
        this.allOrders = [];
        this.allStatusOrders = [];
        this.arrOrderByUserId = [];
    }
    ngOnInit() {
        this.getOrdersOnTime();
        this.statusPipe = '';
    }
    getOrdersOnTime() {
        this.authService.getOrders().subscribe({
            next: response => {
                let meserxId = localStorage.getItem('idUser');
                this.allOrders = response;
                this.arrOrderByUserId = this.allOrders.filter((e) => {
                    return meserxId === e.userId;
                });
                console.log(this.allOrders);
                this.allStatusOrders = this.arrOrderByUserId;
            },
            error: error => {
                console.error('There was an error!', error);
            }
        });
    }
    changeStatus(e) {
        const idOrder = e.target.id;
        console.log(idOrder);
        const status = e.target.value;
        console.log(status);
        let statusOnTime = '';
        switch (status) {
            case 'ready to serve':
                statusOnTime = 'delivered';
                break;
            default:
                Swal.fire({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'You do not have permission to make this status change!',
                });
                break;
        }
        const objStatus = {
            status: statusOnTime
        };
        this.authService.changeStatusOrder(idOrder, objStatus).subscribe({
            next: response => {
                this.getOrdersOnTime();
            }
        });
    }
    changeSectionStatus(e) {
        let statusSection = e.target.dataset.value;
        this.statusPipe = statusSection;
    }
    getTotalPrice(idOfOrder) {
        let priceOfEachProduct;
        let arrPrices;
        this.allStatusOrders.map((e) => {
            if (idOfOrder == e._id) {
                arrPrices = e.products.map((ee) => {
                    priceOfEachProduct = ee.qty * ee.product.price;
                    return priceOfEachProduct;
                });
            }
        });
        this.totalPrice = arrPrices.reduce((acc, num) => acc + num);
        return this.totalPrice;
    }
    cancelOrder(e) {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#30b9d6',
            cancelButtonColor: '#f35f4c',
            confirmButtonText: 'Yes, cancel it!'
        }).then((result) => {
            if (result.isConfirmed) {
                const idOrder = e.target.id;
                const objStatus = {
                    status: 'canceled'
                };
                this.authService.changeStatusOrder(idOrder, objStatus).subscribe({
                    next: response => {
                        this.getOrdersOnTime();
                    }
                });
                Swal.fire('Canceled!', 'The order has been canceled.', 'success');
            }
        });
    }
};
